function switchLang(option) {
  if (!languageContent[option]) {
    console.error("Invalid language:", option);
    return;
  }

  localStorage.setItem("preferredLanguage", option);
  updateLanguageContent(option);
  console.log("Language switched to", option);
}



function updateLanguageContent(lang) {
  requestAnimationFrame(() => {
    for (let key in languageContent[lang]) {
      if (key === "altimetriaImageSrc") {
        const img = document.getElementById("altimetriaImage");
        if (img) img.src = languageContent[lang][key];
      } else if (key === "altimetriaFullHref") {
        const link = document.getElementById("altimetriaLink");
        if (link) link.href = languageContent[lang][key];
      } else {
        const element = document.getElementById(key);
        if (element) element.innerHTML = languageContent[lang][key];
      }
    }

    // Rebind Fancybox to make sure it works on the new link
    if (typeof Fancybox !== "undefined") {
      Fancybox.bind("[data-fancybox]");
    }

    document.body.style.visibility = "visible";
  });
}

function loadPreferredLanguage() {
  const storedLanguage = localStorage.getItem("preferredLanguage");
  const defaultLang = 'pt';
  const languageToLoad = storedLanguage || defaultLang;

  if (!storedLanguage) {
    localStorage.setItem("preferredLanguage", defaultLang);
  }

  updateLanguageContent(languageToLoad); // Initial load
}

window.addEventListener('load', loadPreferredLanguage);